
public class Location {

	private int locX;
	private int locY;
	public Location(int x, int y) {
		// TODO Auto-generated constructor stub
		locX = x;
		locY= y;
	}
	public int getLocationX() {
		return locX;
	}
	public void setLocationX(int locX) {
		this.locX = locX;
	}
	public int getLocationY() {
		return locY;
	}
	public void setLocationY(int locY) {
		this.locY = locY;
	}

}
