package main;

import org.jfree.ui.RefineryUtilities;

import agents.PredatorQL;
import agents.Prey;

public class MainQL {

	public static void main(String[] args) {
		
		QLearning env = new QLearning(new PredatorQL(0, 0),
				new Prey(5, 5));
		env.run();
		
		// Show chart just generated by QLearning
		final Chart c = new Chart("QLearning","output.txt");
        c.pack();
        RefineryUtilities.centerFrameOnScreen(c);
        c.setVisible(true);
	}
}
